import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'

function NewAuthors(props) {

    const { authors, setAuthors } = props;
    const [newAuthor, setNewAuthor] = useState("")
    const navigate = useNavigate();

    const AddNewAuthor = () => {

        axios.post("http://127.0.0.1:8000/authors/new")
            .then(res => {
                setAuthors(...authors, newAuthor)
                navigate('/authors')
            })
            .catch(err => console.log(err))
    };

    const cancelSubmit = () => {
        navigate('/authors')
    }

    return (
        <div>
            <h1>Favorite authors</h1>
            <Link to={'/authors'}>Home</Link>
            <label htmlFor="add a new author">Add a new author:</label>
            <div>
                <p>Name:</p>
                <input type="text" name="newAuthor" id="newAuthor" value={newAuthor} onChange={(e) => setNewAuthor(e.target.value)} />
                <button type="button" onClick={cancelSubmit}>Cancel</button><button type="button" onClick={AddNewAuthor}>Submit</button>
            </div>
        </div>
    )
}

export default NewAuthors
