import React, { useEffect, useState } from 'react'
import { Link, useNavigate, Params, useParams } from 'react-router-dom';
import axios from "axios";

function AuthorUpdate(props) {
    const authorID = useParams();
    const [editAuthorName, seteditAuthorName] = useState()

    useEffect(() => {

        axios.get(`http://127.0.0.1:8000/authors/${authorID}`)
            .then(res => {
                seteditAuthorName(res.data.Name)
            })
    }, []);

    const cancelSubmit = () => {
        navigate('/authors')
    };

    return (
        <div>
            <h1>Favorite authors</h1>
            <Link to={'/authors'}>Home</Link><br />
            <label htmlFor="add a new author">Edit this author:</label>
            <div>
                <p>Name:</p>
                <input type="text" name="newAuthor" id="newAuthor" value={editAuthorName} onChange={(e) => seteditAuthorName(e.target.value)} />
                <button type="button" onClick={cancelSubmit}>Cancel</button><button type="button" onClick={editNewAuthor}>Submit</button>
            </div>
        </div>
    )
}

export default AuthorUpdate
