import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Link, useNavigate } from 'react-router-dom'

function AuthorsDisplay() {

    const navigate = useNavigate();
    const [authors, setAuthors] = useState([]);

    useEffect(() => {

        axios.get("http://127.0.0.1:8000")
            .then(res => setAuthors(res.data))
            .catch(err => console.log(err))
    }, []);

    const deletHandler = () =>{}

    return (
        <div>
            <h1>Favorite authors</h1>
            <Link to={'/authors/new'}>Add an author</Link><br />
            <label htmlFor="authors">We have quotes by:</label>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Author</th>
                        <th>Actions available</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        authors.map((author) => {
                            return (
                                <tr key={author._id}>
                                    <th>{author.name}</th>
                                    <th><button type="button" onClick={navigate(`/author/${author._id}/edit`)}>Edit</button>
                                        <button type="button" onClick={() => { deletHandler(author._id) }}>Delete</button></th>
                                </tr>
                            )
                        })
                    }

                </tbody>
            </table>
        </div>
    )
}

export default AuthorsDisplay
